import React, { useState } from 'react';
import 'css/Login.css';
import 'bootstrap/dist/css/bootstrap.min.css';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [passwordVisible, setPasswordVisible] = useState(false);

  const handleLogin = (e) => {
    e.preventDefault();
    if (email === 'admin@example.com' && password === '123456') {
      alert('Login successful!');
    } else {
      alert('Invalid credentials!');
    }
  };

  const handleForgotPassword = () => {
    if (!email.trim()) {
      alert("E-mail Cannot Be Empty!");
      return;
    }

    fetch("functions/controller/forgotPasswordController.php", {
      method: "POST",
      headers: { "Content-Type": "application/x-www-form-urlencoded" },
      body: new URLSearchParams({
        type: "forgotPassword",
        emailId: email,
      }),
    })
      .then(res => res.text())
      .then((theResponse) => {
        try {
          const obj = JSON.parse(theResponse);
          if (obj.success === true) {
            alert("Please Check Your Email Id!");
            window.location.reload();
          } else {
            alert(obj.errors?.error || "Something went wrong!");
          }
        } catch (err) {
          alert("Invalid response from server.");
        }
      })
      .catch((error) => {
        alert("Failed to send request.");
        console.error(error);
      });
  };

  return (
    <div className="container" style={{ backgroundImage: 'url(images/fcdb8354.png)', minHeight: '100vh', backgroundSize: 'cover' }}>
      <div style={{ marginTop: '140px' }} className="row">
        <div className="col-md-6 col-xm-8 col-md-offset-3">
          <div className="panel panel-primary">
            <div className="panel-heading" style={{ fontSize: '20px' }}>
              <span style={{ color: 'black', fontWeight: 'bold' }}>V</span>endor
              <span style={{ color: 'black', fontWeight: 'bold' }}>B</span>illing
            </div>

            <div className="panel-body">
              <form onSubmit={handleLogin}>
                <div className="form-group">
                  <strong>Please fill in your credentials to login.</strong>
                </div>

                <div className="form-group">
                  <label>Email</label>
                  <div className="input-group">
                    <div className="input-group-addon">
                      <span className="glyphicon glyphicon-envelope"></span>
                    </div>
                    <input
                      type="email"
                      className="form-control"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      id="useEmail"
                      required
                    />
                  </div>
                </div>

                <div className="form-group">
                  <label>Password</label>
                  <div className="input-group">
                    <div className="input-group-addon">
                      <span className="glyphicon glyphicon-lock"></span>
                    </div>
                    <input
                      type={passwordVisible ? "text" : "password"}
                      className="form-control"
                      id="password_toggle"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      required
                    />
                    <span
                      className="input-group-addon"
                      id="smart_eye_cover"
                      onMouseDown={() => setPasswordVisible(true)}
                      onMouseUp={() => setPasswordVisible(false)}
                      title="Click and hold to view password"
                      style={{ cursor: 'pointer' }}
                    >
                      <span
                        id="smarteye"
                        className={`glyphicon ${passwordVisible ? "glyphicon-eye-open" : "glyphicon-eye-close"}`}
                      ></span>
                    </span>
                  </div>
                </div>

                <div className="form-group">
                  <a
                    id="forgotPassword"
                    className="pull-left"
                    style={{ cursor: 'pointer' }}
                    onClick={handleForgotPassword}
                  >
                    Forgot Password?
                  </a>
                  <button type="submit" className="btn btn-primary pull-right">Login</button>
                </div>
              </form>
            </div>
          </div>
          <img src="images/shadow1.png" className="img-responsive" style={{ marginTop: '-20px' }} alt="shadow" />
        </div>
      </div>

      <div style={{ border: '1px solid #D87415', position: 'fixed', bottom: 0, width: '100%', backgroundColor: '#D8741E', padding: '2px' }}></div>
      <div style={{ border: '1px solid #D87415', position: 'fixed', top: 0, width: '100%', backgroundColor: '#D8741E', padding: '1px' }}></div>
    </div>
  );
}

export default Login;
